import org.apache.commons.io.IOUtils;
import org.junit.Test;

import java.io.File;
import java.io.FileOutputStream;
import java.io.IOException;
import java.util.Base64;

import static org.junit.Assert.assertFalse;
import static org.junit.Assert.assertTrue;

public class MsgTest {
//    private String mtomString = "";

    @Test
    public void testDecodeMtom2Zip() throws IOException {
        Msg msg = new Msg();
        FileOutputStream fileOutputStream;
        byte[] data =  Base64.getDecoder().decode(msg.getMtomString());
        assertFalse(String.valueOf(data.length), data.length==0);
        assertTrue(String.valueOf(data.length), data.length==6729);
        fileOutputStream = new FileOutputStream(new File("content.zip"));
        IOUtils.write(data, fileOutputStream);
    }

}
